function getGame($id) {
    if (isset($id)) {
        foreach(getGames() as $game) {
            if ($game['id'] == $id) {
                return $game;
            }
        }
    }
    
    return NULL;
}

function getGameUrl($id) {
    return '/detalle.php?gameId=' . $id;
}
